import React from "react";

export default function Recipe({ title, image, calories, ingredients }) {
  return (
    <div>
      <h2>{title}</h2>
      <img src={image} alt="Recipe image"/>
      <h3>Calories: {calories}</h3>
      <div>Ingredients:</div>
       {Object.entries(ingredients).map((ingredient)=>{
        <p>{ingredient}</p>

      })} 
    </div>
  );
}
